        <div class="m-auto text-center text-white">{{ $this->getSlideName() }}</div>
        <div
            class="flex space-x-4 pl-36"
            x-data
            x-on:keydown.window="
                if ($event.key === 'ArrowLeft') {
                    $wire.call('previousComponent')
                } else if ($event.key === 'ArrowRight') {
                    $wire.call('nextComponent')
                }
            "
        >
