    <div class="pt-12 ">
        <div
            class="w-full overflow-hidden rounded-xl bg-[#292D3E] h-[700px]"
            style="box-shadow: inset 0px -1px 0px rgba(0, 0, 0, 0.5), inset 0px 1px 0px rgba(255, 255, 255, 0.1)"
        >
                @if($showCode)
                    <div class="flex flex-col overflow-x-auto text-sm lg:col-span-4 h-[700px]">
                        <div class="relative">
                            <a href="cursor://file{{ $this->getCurrentComponentFilePath() }}" class="absolute right-0 flex items-center px-4 py-2 ml-auto space-x-2 text-white bg-gray-800 rounded-tr-lg ">
                                <span class="w-3 h-3 bg-red-600 rounded-full"></span>
                                <span>{{ $this->getCurrentComponent() }}</span>
                            </a>
                        </div>
                        <div class="overflow-y-scroll border-b-2 border-gray-700 h-1/2">
                            <pre>
                                <x-torchlight-code contents="{{ $this->getCurrentComponentFilePath() }}" language='php'/>
                            </pre>
                        </div>
                        <div class="flex-1 overflow-y-scroll h-1/2">
                            <div class="sticky top-0 z-10">
                                <a href="cursor://file{{ $this->getCurrentComponentBladeFilePath() }}" class="absolute right-0 flex items-center px-4 py-2 ml-auto space-x-2 text-white bg-gray-800">
                                    <span class="w-3 h-3 bg-red-600 rounded-full"></span>
                                    <span>{{ str_replace('/Users/logancraft/code/tall-present/resources/', '', $this->getCurrentComponentBladeFilePath()) }}</span>
                                </a>
                            </div>
                            <pre class="-mt-2">
                                <x-torchlight-code language='blade'>{!! $this->getCurrentComponentBladeFileContents() !!}</x-torchlight-code>
                            </pre>
                        </div>
                    </div>
                @else
                    <livewire:dynamic-component wire:key='{{ $this->getCurrentComponent() }}' :is="$this->getCurrentComponent()" />
                @endif
        </div>
